import styled from 'styled-components';

const Title = styled.h1`
  margin-top: 31px;
  margin-left: -2.13px;
  padding-top: 5px;
  font-size: 34px;
  line-height: 1.04;
  letter-spacing: -0.015em;

  @media (min-width: 768px) {
    margin-top: 39px;
    margin-left: -2.63px;
    font-size: 42px;
  }
`;

export default Title;
